import React, { useEffect, useState } from 'react'
import {NavLink} from "react-router-dom"
import { ToastContainer } from "react-toastify"
import { handleError } from '../utils'

const Signup = () => {

 
  const [signupInfo, setSignupInfo] = useState({
name:"",
email:"",
password:""
  })

  useEffect(()=>{
    console.log(signupInfo)
  }, [signupInfo])
const handleChange = (e) => {

const {name, value} = e.target;
console.log(name, value)
setSignupInfo({...signupInfo, [name]: value })

} 

const handleSignup =async (e) =>{
e.preventDefault()
const {name,email,password} = signupInfo
if(!name || !email || !password){
 return handleError("name,email,password are required")
}
try{
const url = "http://localhost:8080/auth/signup";
const response = await fetch(url, {
  method: "POST",
  headers: {
    'Content-Type': 'application/json'},
    body: JSON.stringify(signupInfo)
  }
)
const result = await response.json()
console.log(result)
}catch(err){
handleError(err)
}
}
// console.log(loginInfo)
  return (
    <div className='container'>
      <h1>Login</h1>
      <form onSubmit={handleSignup} >
        <div>
        <label htmlFor='name'>Name</label>
  <input onChange={handleChange} type="text" name="name" placeholder='ener your name' autoFocus/>
   </div>
        <div>
        <label htmlFor='email'>Email</label>
  <input onChange={handleChange} type="email" name="email" placeholder='ener your email' />
   </div>
        <div>
        <label htmlFor='password'>Password</label>
  <input onChange={handleChange} type="password" name="password" placeholder='ener your password' />
   </div>

   <button>Signup</button>
   <span>Already have an account</span>
   {/* <Link to ="/login">LOGIN</Link> */}
   <NavLink to="/login" className={({ isActive }) => isActive ? "active-link" : ""}>
  Login
</NavLink>
      </form>
      <ToastContainer />
      <button>ddd</button>
    </div>
  )
}

export default Signup
